#!/usr/bin/env perl

use strict;
use warnings;

use feature qw(say);

my $floor = 0;
my @directions = do {
   split //, <DATA>;
};

foreach my $direction ( @directions ) {
   $floor++ if $direction eq '(';
   $floor-- if $direction eq ')';
}

say $floor;

