#!/usr/bin/env perl

use strict;
use warnings;

use feature qw(say);

my $floor = 0;
my $position = 1;
my @directions = do {
   split //, <DATA>;
};

foreach my $direction ( @directions ) {
   $floor++ if $direction eq '(';
   $floor-- if $direction eq ')';

   last if $floor == -1;
   $position++;
}

say $position;

